function initScrollEvent(){window.addEventListener('scroll',function(event){var scrollTopElement=document.querySelector('.scrolltop');var scrollTop=window.pageYOffset||document.documentElement.scrollTop||document.body.scrollTop||0;if(scrollTop>50){scrollTopElement.classList.remove('hidden')}else{scrollTopElement.classList.add('hidden')}})}
function initSmoothScroll(){var scroll=new SmoothScroll('.js-scroll',{speed:500,speedAsDuration:!0})}
function init(){initScrollEvent();initSmoothScroll()}
init()